import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { moviesAPI } from '../services/api';
import MovieRow from '../components/MovieRow';

const Home = () => {
  const [featuredMovie, setFeaturedMovie] = useState(null);
  const [trendingMovies, setTrendingMovies] = useState([]);
  const [actionMovies, setActionMovies] = useState([]);
  const [dramaMovies, setDramaMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [trending, action, drama, allMovies] = await Promise.all([
          moviesAPI.getTrendingMovies(10),
          moviesAPI.getMoviesByGenre('Action', 15),
          moviesAPI.getMoviesByGenre('Drama', 15),
          moviesAPI.getMovies({ limit: 1 })
        ]);

        setTrendingMovies(trending.data.movies);
        setActionMovies(action.data.movies);
        setDramaMovies(drama.data.movies);

        // Set first movie as featured
        if (allMovies.data.movies.length > 0) {
          setFeaturedMovie(allMovies.data.movies[0]);
        }

        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handlePlayMovie = () => {
    if (featuredMovie) {
      navigate(`/movie/${featuredMovie._id}`);
    }
  };

  const handleAddToWatchlist = async () => {
    if (featuredMovie) {
      try {
        await moviesAPI.addToWatchlist(featuredMovie._id);
        alert('Added to watchlist!');
      } catch (error) {
        console.error('Error adding to watchlist:', error);
      }
    }
  };

  if (loading) {
    return <div className="loading"><div className="spinner"></div></div>;
  }

  return (
    <div className="home">
      {/* Hero Section */}
      {featuredMovie && (
        <div 
          className="hero"
          style={{
            backgroundImage: `linear-gradient(rgba(0,0,0,0.4), rgba(0,0,0,0.8)), url(${featuredMovie.thumbnail_url})`
          }}
        >
          <div className="hero-content">
            <h1 className="hero-title">{featuredMovie.title}</h1>
            <p className="hero-description">{featuredMovie.description}</p>
            <div className="hero-buttons">
              <button className="btn btn-primary" onClick={handlePlayMovie}>
                â–¶ Play
              </button>
              <button className="btn btn-secondary" onClick={handleAddToWatchlist}>
                + My List
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Movie Rows */}
      <div className="movie-rows">
        {trendingMovies.length > 0 && (
          <MovieRow title="Trending Now" movies={trendingMovies} />
        )}
        {actionMovies.length > 0 && (
          <MovieRow title="Action Movies" movies={actionMovies} />
        )}
        {dramaMovies.length > 0 && (
          <MovieRow title="Drama Series" movies={dramaMovies} />
        )}
      </div>
    </div>
  );
};

export default Home;
